import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";

// Connect to backend Socket.io
const socket = io("http://localhost:5000");

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all orders from backend
  const fetchOrders = () => {
    axios.get("http://localhost:5000/api/order")
      .then(res => {
        setOrders(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchOrders();

    // Listen for new orders
    socket.on("newOrder", (order) => {
      setOrders(prev => [...prev, order]);
    });

    // Listen for order updates (status changes)
    socket.on("updateOrder", (updatedOrder) => {
      setOrders(prev =>
        prev.map(o => o._id === updatedOrder._id ? updatedOrder : o)
      );
    });

    // Clean up listeners on unmount
    return () => {
      socket.off("newOrder");
      socket.off("updateOrder");
    };
  }, []);

  // Update order status
  const updateStatus = (id, status) => {
    axios.put(`http://localhost:5000/api/order/${id}`, { status })
      .catch(err => console.log(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Admin Dashboard - Orders</h2>
      {loading ? (
        <p>Loading orders...</p>
      ) : orders.length === 0 ? (
        <p>No orders yet</p>
      ) : (
        orders.map(order => (
          <div key={order._id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
            <p><strong>Table:</strong> {order.tableNumber}</p>
            <p><strong>Status:</strong> {order.status || "pending"}</p>
            <p><strong>Items:</strong></p>
            <ul>
              {order.items.map(i => (
                <li key={i.menuId._id}>
                  {i.menuId.name} x{i.quantity}
                </li>
              ))}
            </ul>
            {order.status !== "completed" && (
              <button onClick={() => updateStatus(order._id, "completed")}>
                Mark Completed
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default AdminOrders;
