import React, { useEffect, useState } from "react";
import axios from "axios";

const Menu = () => {
  const query = new URLSearchParams(window.location.search);
  const tableNumber = query.get("table") || "Unknown";

  const [menu, setMenu] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5000/api/menu")
      .then(res => {
        setMenu(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setLoading(false);
      });
  }, []);

  const handleOrder = (item) => {
    axios.post("http://localhost:5000/api/order", {
      tableNumber,
      items: [{ menuId: item._id, quantity: 1 }],
      totalAmount: item.price
    })
    .then(res => alert("Order placed!"))
    .catch(err => console.log(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Menu for Table {tableNumber}</h2>
      {loading ? (
        <p>Loading menu...</p>
      ) : menu.length === 0 ? (
        <p>No items available</p>
      ) : (
        menu.map(item => (
          <div key={item._id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
            <h3>{item.name} - â‚¹{item.price}</h3>
            <p>Stock: {item.stock}, Estimated Time: {item.estimatedTime} mins</p>
            <button onClick={() => handleOrder(item)}>Order</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Menu;
