// src/components/Menu.js
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";
import './Menu.css'; // import your new CSS file

const socket = io("http://localhost:5000");

function Menu() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tableNumber = queryParams.get("table");

  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState({});

  useEffect(() => {
    // Initial fetch of the menu
    axios.get("http://localhost:5000/api/menu")
      .then((res) => setMenu(res.data))
      .catch((err) => console.error(err));

    // Listen for real-time stock updates
    socket.on("stockUpdate", (updatedItems) => {
      setMenu(prevMenu =>
        prevMenu.map(item => {
          const updated = updatedItems.find(u => u._id === item._id);
          return updated ? updated : item;
        })
      );
    });

    return () => {
      socket.off("stockUpdate");
    };
  }, []);

  const addToCart = (item) => {
    setCart(prevCart => {
      const quantity = (prevCart[item._id]?.quantity || 0) + 1;
      return {
        ...prevCart,
        [item._id]: { ...item, quantity }
      };
    });
  };

  const placeOrder = async () => {
    if (Object.keys(cart).length === 0) {
      alert("Your cart is empty!");
      return;
    }

    const orderItems = Object.values(cart).map(item => ({
      menuId: item._id,
      quantity: item.quantity
    }));

    const totalAmount = Object.values(cart).reduce(
      (total, item) => total + item.price * item.quantity, 0
    );

    try {
      await axios.post("http://localhost:5000/api/orders", {
        tableNumber: Number(tableNumber),
        items: orderItems,
        totalAmount
      });
      alert("Order placed successfully! ✅");
      setCart({});
    } catch (err) {
      console.error(err);
      alert("Failed to place order. Please check stock. ❌");
    }
  };

  return (
    <div className="menu-container">
      <h2 className="menu-header">Menu for Table {tableNumber}</h2>
      <ul className="menu-list">
        {menu.map((item) => (
          <li key={item._id} className="menu-item">
            <div>
              <div className="item-name">{item.name}</div>
              <div className="item-details">
                ₹{item.price} | Stock: {item.stock} left | Est. Time: {item.estimatedTime} mins
              </div>
            </div>
            {item.stock > 0 ? (
              <button className="add-button" onClick={() => addToCart(item)}>
                Add to Cart
              </button>
            ) : (
              <button className="add-button" disabled>Out of Stock</button>
            )}
          </li>
        ))}
      </ul>
      
      {/* Cart Summary */}
      {Object.keys(cart).length > 0 && (
        <div style={{ marginTop: "40px", borderTop: "2px solid #ccc", paddingTop: "20px" }}>
          <h3>Your Cart</h3>
          <ul>
            {Object.values(cart).map(item => (
              <li key={item._id}>
                {item.name} x {item.quantity} - ₹{item.price * item.quantity}
              </li>
            ))}
          </ul>
          <p><strong>Total: ₹{Object.values(cart).reduce((sum, item) => sum + item.price * item.quantity, 0)}</strong></p>
          <button onClick={placeOrder} className="add-button">Place Order</button>
        </div>
      )}
    </div>
  );
}

export default Menu;
