// src/components/AdminOrders.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import "./AdminOrders.css";

const socket = io("http://localhost:5000");

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/orders");
      setOrders(res.data);
    } catch (err) {
      console.error("Error fetching orders:", err);
    }
  };

  useEffect(() => {
    fetchOrders();

    socket.on("newOrder", (newOrder) => {
      // Add new orders to the front of the list
      setOrders(prev => [newOrder, ...prev]);
    });

    socket.on("updateOrder", (updatedOrder) => {
      // Update the status of an existing order
      setOrders(prev => prev.map(o => o._id === updatedOrder._id ? updatedOrder : o));
    });

    return () => {
      socket.off("newOrder");
      socket.off("updateOrder");
    };
  }, []);

  const updateStatus = async (id, status) => {
    try {
      await axios.put(`http://localhost:5000/api/orders/${id}`, { status });
    } catch (err) {
      console.error("Error updating order status:", err);
    }
  };

  return (
    <div className="admin-orders-container">
      <h2>Live Orders Dashboard</h2>
      <div className="orders-grid">
        {orders.length === 0 ? (
          <p className="no-orders-message">No new orders yet. Time for a break! ☕</p>
        ) : (
          orders.map(order => (
            <div 
                key={order._id} 
                className={`order-card status-${order.status}`}
            >
              <h4 className="table-header">Table {order.tableNumber}</h4>
              <p className="status-badge">Status: <strong>{order.status.toUpperCase()}</strong></p>
              <ul className="order-items-list">
                {order.items.map(i => (
                  <li key={i._id}>{i.quantity} x {i.menuId ? i.menuId.name : 'Unknown Item'}</li>
                ))}
              </ul>
              <div className="order-footer">
                <span className="order-total">Total: ₹{order.totalAmount}</span>
                <div className="order-actions">
                  {order.status === "pending" && (
                    <button onClick={() => updateStatus(order._id, "in-progress")} className="action-in-progress">Start Prep</button>
                  )}
                  {order.status === "in-progress" && (
                    <button onClick={() => updateStatus(order._id, "completed")} className="action-completed">Mark Done</button>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AdminOrders;
