// src/components/Menu.js
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";
import "./Menu.css";

// Create socket connection
const socket = io("http://localhost:5000");

function Menu() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tableNumber = queryParams.get("table");

  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState({});
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // Fetch menu items
    const fetchMenu = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/menu");
        setMenu(res.data);
      } catch (err) {
        console.error("Failed to fetch menu:", err);
        alert("Failed to load menu. Please refresh the page.");
      }
    };

    fetchMenu();

    // Socket event listeners
    socket.on("connect", () => {
      console.log("‚úÖ Connected to server");
      setIsConnected(true);
    });

    socket.on("disconnect", () => {
      console.log("‚ùå Disconnected from server");
      setIsConnected(false);
    });

    socket.on("stockUpdate", (updatedItems) => {
      console.log("üì¶ Received stock update:", updatedItems);
      setMenu(prevMenu =>
        prevMenu.map(item => {
          const updated = updatedItems.find(u => u._id === item._id);
          return updated ? updated : item;
        })
      );
    });

    socket.on("orderConfirmed", (data) => {
      console.log("‚úÖ Order confirmed:", data);
      if (data.success) {
        alert("Order placed successfully! ‚úÖ");
        setCart({});
      } else {
        alert(`Order failed: ${data.message}`);
      }
      setIsLoading(false);
    });

    // Table-specific order confirmation
    socket.on(`table${tableNumber}`, (data) => {
      if (data.type === 'orderConfirmed') {
        alert(`‚úÖ ${data.message}`);
        setCart({});
        setIsLoading(false);
      }
    });

    return () => {
      socket.off("connect");
      socket.off("disconnect");
      socket.off("stockUpdate");
      socket.off("orderConfirmed");
      socket.off(`table${tableNumber}`);
    };
  }, [tableNumber]);

  const addToCart = (item) => {
    if (item.stock <= 0) {
      alert("This item is out of stock!");
      return;
    }

    setCart(prevCart => {
      const currentQuantity = prevCart[item._id]?.quantity || 0;
      
      // Check if adding would exceed stock
      if (currentQuantity + 1 > item.stock) {
        alert(`Only ${item.stock} ${item.name} available!`);
        return prevCart;
      }

      const quantity = currentQuantity + 1;
      return {
        ...prevCart,
        [item._id]: { 
          ...item, 
          quantity 
        }
      };
    });
  };

  const removeFromCart = (itemId) => {
    setCart(prevCart => {
      const newCart = { ...prevCart };
      delete newCart[itemId];
      return newCart;
    });
  };

  const updateCartQuantity = (itemId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(itemId);
      return;
    }

    const item = cart[itemId];
    if (newQuantity > item.stock) {
      alert(`Only ${item.stock} ${item.name} available!`);
      return;
    }

    setCart(prevCart => ({
      ...prevCart,
      [itemId]: {
        ...prevCart[itemId],
        quantity: newQuantity
      }
    }));
  };

  const placeOrder = async () => {
    if (Object.keys(cart).length === 0) {
      alert("Your cart is empty!");
      return;
    }

    if (!isConnected) {
      alert("Not connected to server. Please wait...");
      return;
    }

    // Safety check: ensure all items have a valid ID before mapping
    const orderItems = Object.values(cart)
      .filter(item => item && item._id && item.quantity > 0)
      .map(item => ({
        menuId: item._id,
        quantity: item.quantity,
        name: item.name // Include name for backend reference
      }));

    // If the safety check filters out all items, stop here.
    if (orderItems.length === 0) {
      alert("Cannot place order. Please add valid items to your cart.");
      setCart({});
      return;
    }

    // Calculate total amount (both frontend and backend will calculate for verification)
    const totalAmount = Object.values(cart).reduce((sum, item) => sum + (item.price * item.quantity), 0);

    setIsLoading(true);

    try {
      // Option 1: Use HTTP API (your current approach)
      const response = await axios.post("http://localhost:5000/api/orders", {
        tableNumber: Number(tableNumber),
        items: orderItems,
        totalAmount
      });

      if (response.data.success) {
        console.log("Order submitted successfully via HTTP:", response.data);
        // The socket event will handle the success message
      } else {
        throw new Error(response.data.error);
      }

    } catch (err) {
      console.error("Order submission failed:", err.response?.data || err.message);
      
      let errorMessage = "Order failed. Please try again.";
      if (err.response?.data?.error) {
        errorMessage = err.response.data.error;
      } else if (err.message.includes("Network Error")) {
        errorMessage = "Cannot connect to server. Please check your connection.";
      }
      
      alert(errorMessage);
      setIsLoading(false);
    }
  };

  const totalAmount = Object.values(cart).reduce((sum, item) => sum + item.price * item.quantity, 0);
  const totalItems = Object.values(cart).reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="menu-page-wrapper">
      <div className="menu-container">
        <div className="page-header">
          <h2 className="page-title">Menu for Table {tableNumber}</h2>
          <div className="connection-status">
            {isConnected ? "‚úÖ Connected" : "‚ùå Connecting..."}
          </div>
        </div>
        
        <div className="menu-grid">
          {menu.map((item) => (
            <div key={item._id} className="menu-card">
              <h3 className="item-name">{item.name}</h3>
              <div className="item-details-row">
                <span className="item-price">‚Çπ{item.price}</span>
                <span className={`item-stock status-${item.stock > 10 ? 'high' : item.stock > 0 ? 'low' : 'out'}`}>
                  Stock: {item.stock} left
                </span>
              </div>
              <p className="item-time">Est. Time: {item.estimatedTime} mins</p>
              
              <div className="card-actions">
                {item.stock > 0 ? (
                  <button 
                    className="add-button" 
                    onClick={() => addToCart(item)}
                    disabled={cart[item._id]?.quantity >= item.stock}
                  >
                    {cart[item._id]?.quantity >= item.stock ? 'Max Stock' : 'Add to Cart'}
                  </button>
                ) : (
                  <button className="add-button disabled" disabled>Out of Stock</button>
                )}
                
                {cart[item._id] && (
                  <div className="cart-controls">
                    <button onClick={() => updateCartQuantity(item._id, cart[item._id].quantity - 1)}>-</button>
                    <span>{cart[item._id].quantity}</span>
                    <button 
                      onClick={() => updateCartQuantity(item._id, cart[item._id].quantity + 1)}
                      disabled={cart[item._id].quantity >= item.stock}
                    >+</button>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Sticky Cart Summary */}
      {Object.keys(cart).length > 0 && (
        <div className="sticky-cart-summary">
          <div className="cart-header">
            <h4>Your Order ({totalItems} items)</h4>
            <button 
              className="clear-cart-btn"
              onClick={() => setCart({})}
            >
              Clear All
            </button>
          </div>
          <ul className="cart-list">
            {Object.values(cart).map(item => (
              <li key={item._id}>
                <div className="cart-item">
                  <span className="item-name">{item.name}</span>
                  <div className="item-controls">
                    <button onClick={() => updateCartQuantity(item._id, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button 
                      onClick={() => updateCartQuantity(item._id, item.quantity + 1)}
                      disabled={item.quantity >= item.stock}
                    >+</button>
                  </div>
                  <span className="item-total">‚Çπ{item.price * item.quantity}</span>
                </div>
              </li>
            ))}
          </ul>
          <div className="cart-footer">
            <div className="total-row">
              <span className="cart-total-label">Total:</span>
              <span className="cart-total-amount">‚Çπ{totalAmount}</span>
            </div>
            <button 
              onClick={placeOrder} 
              className="place-order-button"
              disabled={isLoading || !isConnected}
            >
              {isLoading ? "Placing Order..." : "Place Order"}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Menu;
