import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Menu from "./components/Menu";
import SelectTable from "./components/SelectTable";
import SingleQR from "./components/SingleQR"; 
import AdminLogin from "./components/AdminLogin";
import AdminDashboard from "./components/AdminDashboard";
import AddMenuItem from "./components/AddMenuItem";

<Route path="/admin/add-item" element={<AddMenuItem />} />

function App() {
  return (
    <Router>
      <Routes>
        {/* Customer Flow */}
        <Route path="/" element={<SelectTable />} />  {/* customers start here */}
        <Route path="/menu" element={<Menu />} />

        {/* Admin Flow */}
        <Route path="/admin" element={<AdminLogin />} />
        <Route path="/admin/dashboard" element={<AdminDashboard />} />

        {/* QR Generator (only for printing by admin, not customer-facing) */}
        <Route path="/qr" element={<SingleQR />} />
      </Routes>
    </Router>
  );
}

export default App;
