import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Menu from "./components/Menu";
import AdminOrders from "./components/AdminOrders";
import SelectTable from "./components/SelectTable";
import SingleQR from "./components/SingleQR"; // Optional: to display the QR on screen

function App() {
  return (
    <Router>
      <Routes>
        {/* Default page: show QR or table selection */}
        <Route path="/" element={<SelectTable />} />
        <Route path="/select-table" element={<SelectTable />} />

        {/* Menu page for customers */}
        <Route path="/menu" element={<Menu />} />

        {/* Admin dashboard */}
        <Route path="/admin" element={<AdminOrders />} />

        {/* Optional: Page showing the single QR code */}
        <Route path="/qr" element={<SingleQR />} />

        {/* Fallback route */}
        <Route path="*" element={<SelectTable />} />
      </Routes>
    </Router>
  );
}

export default App;
