import React, { useEffect, useState } from "react";
import axios from "axios";

const Menu = () => {
  const query = new URLSearchParams(window.location.search);
  const tableNumber = query.get("table");

  const [menu, setMenu] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/menu")
      .then(res => setMenu(res.data))
      .catch(err => console.log(err));
  }, []);

  const handleOrder = (item) => {
    axios.post("http://localhost:5000/api/order", {
      tableNumber,
      items: [{ menuId: item._id, quantity: 1 }],
      totalAmount: item.price
    })
    .then(res => alert("Order placed!"))
    .catch(err => console.log(err));
  };

  return (
    <div>
      <h2>Menu for Table {tableNumber}</h2>
      {menu.map(item => (
        <div key={item._id}>
          <h3>{item.name} - ${item.price}</h3>
          <p>Stock: {item.stock}, Time: {item.estimatedTime} mins</p>
          <button onClick={() => handleOrder(item)}>Order</button>
        </div>
      ))}
    </div>
  );
};

export default Menu;
