// src/components/MenuPage.js
import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./MenuPage.css";

const MenuPage = () => {
  const { tableNumber } = useParams();
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);

  const menuItems = [
    { id: 1, name: "Samosa", price: 10, image: "ü´ì", estTime: 3, stock: 11 },
    { id: 2, name: "Pani Puri", price: 60, image: "ü•†", estTime: 5, stock: 8 },
    { id: 3, name: "Masala Dosa", price: 80, image: "ü•ò", estTime: 10, stock: 6 },
    { id: 4, name: "Biryani", price: 120, image: "üçõ", estTime: 15, stock: 12 },
    { id: 5, name: "Butter Chicken", price: 150, image: "üçó", estTime: 20, stock: 7 },
    { id: 6, name: "Naan", price: 30, image: "ü´ì", estTime: 5, stock: 15 }
  ];

  const addToCart = (item) => {
    if (item.stock <= 0) {
      alert("Sorry, this item is out of stock!");
      return;
    }

    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    if (existingItem) {
      if (existingItem.quantity >= item.stock) {
        alert(`Only ${item.stock} items available in stock!`);
        return;
      }
      setCart(cart.map(cartItem =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(item => item.id !== itemId));
  };

  const updateQuantity = (itemId, newQuantity) => {
    if (newQuantity === 0) {
      removeFromCart(itemId);
    } else {
      const item = menuItems.find(menuItem => menuItem.id === itemId);
      if (newQuantity > item.stock) {
        alert(`Only ${item.stock} items available in stock!`);
        return;
      }
      setCart(cart.map(item =>
        item.id === itemId ? { ...item, quantity: newQuantity } : item
      ));
    }
  };

  const clearCart = () => {
    setCart([]);
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const placeOrder = async () => {
    if (cart.length === 0) {
      alert("Your cart is empty!");
      return;
    }

    // Prepare order data in the format your backend expects
    const orderData = {
      tableNumber: parseInt(tableNumber),
      items: cart.map(item => ({
        menuId: item.id,
        name: item.name,
        quantity: item.quantity,
        price: item.price
      })),
      totalAmount: getTotalPrice(),
      status: "pending"
    };

    console.log("Sending order data:", orderData);

    try {
      // Send order to backend
      const response = await axios.post("http://localhost:5000/api/orders", orderData);
      
      if (response.data.success) {
        alert(`Order placed successfully for Table ${tableNumber}!`);
        setCart([]);
        navigate("/select-table");
      } else {
        alert("Failed to place order: " + (response.data.message || "Unknown error"));
      }
    } catch (error) {
      console.error("Error placing order:", error);
      if (error.response) {
        alert(`Error: ${error.response.data.message || "Failed to place order"}`);
      } else {
        alert("Network error. Please check if the server is running.");
      }
    }
  };

  return (
    <div className="menu-page">
      <div className="menu-header">
        <button onClick={() => navigate("/select-table")} className="back-btn">
          ‚Üê Back to Tables
        </button>
        <h1>Hotel MC - Table {tableNumber}</h1>
        <div className="cart-summary">
          Cart: {cart.reduce((total, item) => total + item.quantity, 0)} items
        </div>
      </div>

      <div className="menu-content">
        <div className="menu-items">
          <h2>Our Menu</h2>
          <div className="items-grid">
            {menuItems.map(item => (
              <div key={item.id} className="menu-item">
                <div className="item-image">{item.image}</div>
                <div className="item-details">
                  <h3>{item.name}</h3>
                  <div className="item-info">
                    <span className="est-time">Est. Time: {item.estTime} mins</span>
                    <span className="item-price">${item.price}</span>
                    <span className="stock">STOCK: {item.stock} LEFT</span>
                  </div>
                </div>
                <button 
                  onClick={() => addToCart(item)}
                  className="add-to-cart-btn"
                  disabled={item.stock === 0}
                >
                  {item.stock === 0 ? "Out of Stock" : "Add to Cart"}
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="cart-section">
          <div className="cart-header">
            <h2>Your Order ({cart.reduce((total, item) => total + item.quantity, 0)} items)</h2>
            {cart.length > 0 && (
              <button onClick={clearCart} className="clear-btn">
                Clear All
              </button>
            )}
          </div>
          
          <div className="cart-items">
            {cart.length === 0 ? (
              <p className="empty-cart">Your cart is empty</p>
            ) : (
              cart.map(item => (
                <div key={item.id} className="cart-item">
                  <div className="cart-item-details">
                    <span className="item-name">{item.name}</span>
                    <span className="item-price">${item.price} each</span>
                  </div>
                  <div className="quantity-controls">
                    <button 
                      onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      className="quantity-btn"
                    >
                      -
                    </button>
                    <span className="quantity">{item.quantity}</span>
                    <button 
                      onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      className="quantity-btn"
                    >
                      +
                    </button>
                    <button 
                      onClick={() => removeFromCart(item.id)}
                      className="remove-btn"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                  <div className="item-total">
                    ${item.price * item.quantity}
                  </div>
                </div>
              ))
            )}
          </div>
          
          {cart.length > 0 && (
            <div className="order-summary">
              <div className="total-price">
                <strong>Total: ${getTotalPrice()}</strong>
              </div>
              <button 
                onClick={placeOrder}
                className="place-order-btn"
              >
                Place Order
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Menu;
