// src/components/Menu.js
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";
import "./Menu.css";

function Menu() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tableNumber = queryParams.get("table");

  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState({});
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Fetch menu items
    const fetchMenu = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/menu");
        setMenu(res.data);
      } catch (err) {
        console.error("Failed to fetch menu:", err);
        alert("Failed to load menu. Please refresh the page.");
      }
    };

    fetchMenu();

    // Initialize socket connection
    const newSocket = io("http://localhost:5000", {
      transports: ['websocket', 'polling']
    });
    setSocket(newSocket);

    // Socket event listeners
    newSocket.on("connect", () => {
      console.log("âœ… Connected to server");
      setIsConnected(true);
    });

    newSocket.on("disconnect", () => {
      console.log("âŒ Disconnected from server");
      setIsConnected(false);
    });

    newSocket.on("connect_error", (error) => {
      console.error("âŒ Connection error:", error);
      setIsConnected(false);
    });

    newSocket.on("stockUpdate", (updatedItems) => {
      console.log("ðŸ“¦ Received stock update:", updatedItems);
      setMenu(prevMenu =>
        prevMenu.map(item => {
          const updated = updatedItems.find(u => u._id === item._id);
          return updated ? updated : item;
        })
      );
    });

    newSocket.on("orderConfirmed", (data) => {
      console.log("âœ… Order confirmed:", data);
      if (data.success) {
        alert("Order placed successfully! âœ…");
        setCart({});
      } else {
        alert(`Order failed: ${data.message}`);
      }
      setIsLoading(false);
    });

    return () => {
      newSocket.close();
    };
  }, [tableNumber]);

  const addToCart = (item) => {
    if (item.stock <= 0) {
      alert("This item is out of stock!");
      return;
    }

    setCart(prevCart => {
      const currentQuantity = prevCart[item._id]?.quantity || 0;
      
      // Check if adding would exceed stock
      if (currentQuantity + 1 > item.stock) {
        alert(`Only ${item.stock} ${item.name} available!`);
        return prevCart;
      }

      const quantity = currentQuantity + 1;
      return {
        ...prevCart,
        [item._id]: { 
          ...item, 
          quantity 
        }
      };
    });
  };

  const removeFromCart = (itemId) => {
    setCart(prevCart => {
      const newCart = { ...prevCart };
      delete newCart[itemId];
      return newCart;
    });
  };

  const updateCartQuantity = (itemId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(itemId);
      return;
    }

    const item = cart[itemId];
    if (newQuantity > item.stock) {
      alert(`Only ${item.stock} ${item.name} available!`);
      return;
    }

    setCart(prevCart => ({
      ...prevCart,
      [itemId]: {
        ...prevCart[itemId],
        quantity: newQuantity
      }
    }));
  };

  c// In your Menu.js - Update the placeOrder function
const placeOrder = async () => {
  if (Object.keys(cart).length === 0) {
    alert("Your cart is empty!");
    return;
  }

  if (!isConnected) {
    alert("Not connected to server. Please wait...");
    return;
  }

  // Prepare order items in correct format
  const orderItems = Object.values(cart)
    .filter(item => item && item._id && item.quantity > 0)
    .map(item => ({
      menuId: item._id,
      name: item.name,
      quantity: item.quantity,
      price: item.price  // Make sure to include price
    }));

  if (orderItems.length === 0) {
    alert("Cannot place order. Please add valid items to your cart.");
    setCart({});
    return;
  }

  const totalAmount = Object.values(cart).reduce((sum, item) => sum + (item.price * item.quantity), 0);

  setIsLoading(true);

  try {
    console.log("Placing order with data:", {
      tableNumber: Number(tableNumber),
      items: orderItems,
      totalAmount
    });

    const response = await axios.post("http://localhost:5000/api/orders", {
      tableNumber: Number(tableNumber),
      items: orderItems,
      totalAmount,
      status: "pending"  // Add status field
    });

    console.log("Order response:", response.data);

    if (response.data.success) {
      alert("Order placed successfully! âœ…");
      setCart({});
      
      // Emit socket event for real-time update
      if (socket) {
        socket.emit("newOrder", response.data.order);
      }
    } else {
      throw new Error(response.data.message || "Order failed");
    }

  } catch (err) {
    console.error("Order submission failed:", err);
    
    let errorMessage = "Order failed. Please try again.";
    if (err.response?.data?.message) {
      errorMessage = err.response.data.message;
    } else if (err.response?.data?.error) {
      errorMessage = err.response.data.error;
    } else if (err.message.includes("Network Error")) {
      errorMessage = "Cannot connect to server. Please check if backend is running on port 5000.";
    }
    
    alert(`Error: ${errorMessage}`);
  } finally {
    setIsLoading(false);
  }
};

  const totalAmount = Object.values(cart).reduce((sum, item) => sum + item.price * item.quantity, 0);
  const totalItems = Object.values(cart).reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="menu-page-wrapper">
      <div className="menu-container">
        <div className="page-header">
          <div>
            <h1 className="page-title">Hotel Management System</h1>
            <p className="page-subtitle">Menu for Table {tableNumber}</p>
          </div>
          <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
            <span className="status-dot"></span>
            {isConnected ? 'Connected' : 'Connecting...'}
          </div>
        </div>
        
        <div className="menu-grid">
          {menu.map((item) => (
            <div key={item._id} className="menu-card">
              <div className="item-image"></div>
              <div className="card-content">
                <div className="item-header">
                  <h3 className="item-name">{item.name}</h3>
                  <span className="item-price">${item.price}</span>
                </div>
                <div className="item-details">
                  <span className="item-time">Est. Time: {item.estimatedTime} mins</span>
                  <span className={`item-stock status-${item.stock > 10 ? 'high' : item.stock > 0 ? 'low' : 'out'}`}>
                    Stock: {item.stock} left
                  </span>
                </div>
                
                <div className="card-actions">
                  {item.stock > 0 ? (
                    <button 
                      className="add-button" 
                      onClick={() => addToCart(item)}
                      disabled={cart[item._id]?.quantity >= item.stock}
                    >
                      {cart[item._id]?.quantity >= item.stock ? 'Max Stock' : 'Add to Cart'}
                    </button>
                  ) : (
                    <button className="add-button disabled" disabled>Out of Stock</button>
                  )}
                  
                  {cart[item._id] && (
                    <div className="cart-controls">
                      <div className="quantity-controls">
                        <button 
                          className="quantity-btn"
                          onClick={() => updateCartQuantity(item._id, cart[item._id].quantity - 1)}
                        >
                          -
                        </button>
                        <span className="quantity-display">{cart[item._id].quantity}</span>
                        <button 
                          className="quantity-btn"
                          onClick={() => updateCartQuantity(item._id, cart[item._id].quantity + 1)}
                          disabled={cart[item._id].quantity >= item.stock}
                        >
                          +
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Sticky Cart Summary */}
      {Object.keys(cart).length > 0 && (
        <div className="sticky-cart-summary">
          <div className="cart-header">
            <h4>Your Order ({totalItems} items)</h4>
            <button 
              className="clear-cart-btn"
              onClick={() => setCart({})}
            >
              Clear All
            </button>
          </div>
          <div className="cart-content">
            <ul className="cart-list">
              {Object.values(cart).map(item => (
                <li key={item._id} className="cart-item">
                  <div className="cart-item-info">
                    <div className="cart-item-name">{item.name}</div>
                    <div className="cart-item-price">${item.price} each</div>
                  </div>
                  <div className="cart-item-controls">
                    <button 
                      className="cart-quantity-btn"
                      onClick={() => updateCartQuantity(item._id, item.quantity - 1)}
                    >
                      -
                    </button>
                    <span className="cart-quantity">{item.quantity}</span>
                    <button 
                      className="cart-quantity-btn"
                      onClick={() => updateCartQuantity(item._id, item.quantity + 1)}
                      disabled={item.quantity >= item.stock}
                    >
                      +
                    </button>
                  </div>
                  <div className="cart-item-total">${item.price * item.quantity}</div>
                </li>
              ))}
            </ul>
          </div>
          <div className="cart-footer">
            <div className="total-row">
              <span className="cart-total-label">Total:</span>
              <span className="cart-total-amount">${totalAmount}</span>
            </div>
            <button 
              onClick={placeOrder} 
              className="place-order-button"
              disabled={isLoading || !isConnected}
            >
              {isLoading ? "Placing Order..." : "Place Order"}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Menu;
