// src/components/AdminOrders.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import "./AdminOrders.css";

const socket = io("http://localhost:5000");

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Sample data to test the UI
  const sampleOrders = [
    {
      _id: "1",
      tableNumber: 1,
      status: "pending",
      items: [
        { name: "Somosa", quantity: 1, price: 40 },
        { name: "Pani Puri", quantity: 2, price: 60 }
      ],
      totalAmount: 210
    },
    {
      _id: "2",
      tableNumber: 2,
      status: "pending",
      items: [
        { name: "Pani Puri", quantity: 1, price: 60 },
        { name: "Somosa", quantity: 2, price: 40 }
      ],
      totalAmount: 240
    },
    {
      _id: "3",
      tableNumber: 3,
      status: "pending",
      items: [
        { name: "Somosa", quantity: 1, price: 40 },
        { name: "Pani Puri", quantity: 2, price: 60 }
      ],
      totalAmount: 250
    }
  ];

  const fetchOrders = async () => {
    try {
      setLoading(true);
      // First try with sample data to test UI
      setOrders(sampleOrders);
      
      // Then try to fetch from API
      const res = await axios.get("http://localhost:5000/api/orders");
      console.log("API Response:", res.data);
      
      if (res.data && Array.isArray(res.data)) {
        setOrders(res.data);
      } else if (res.data && Array.isArray(res.data.orders)) {
        setOrders(res.data.orders);
      } else if (res.data && Array.isArray(res.data.data)) {
        setOrders(res.data.data);
      }
    } catch (err) {
      console.error("Error fetching orders:", err);
      // Keep sample data if API fails
      console.log("Using sample data due to API error");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();

    // Socket listeners for real-time updates
    socket.on("newOrder", (newOrder) => {
      console.log("New order received:", newOrder);
      setOrders(prev => [newOrder, ...prev]);
    });

    socket.on("updateOrder", (updatedOrder) => {
      console.log("Order updated:", updatedOrder);
      setOrders(prev => prev.map(o => o._id === updatedOrder._id ? updatedOrder : o));
    });

    return () => {
      socket.off("newOrder");
      socket.off("updateOrder");
    };
  }, []);

  const updateStatus = async (orderId, status) => {
    try {
      console.log("Updating order:", orderId, "to status:", status);
      
      // Update local state immediately for better UX
      setOrders(prev => prev.map(order => 
        order._id === orderId ? { ...order, status } : order
      ));

      // Try to update via API
      const response = await axios.put(`http://localhost:5000/api/orders/${orderId}`, { status });
      console.log("Update response:", response.data);

      if (!response.data.success) {
        // Revert if API call failed
        setOrders(prev => prev.map(order => 
          order._id === orderId ? { ...order, status: "pending" } : order
        ));
        alert("Failed to update order status");
      }
    } catch (err) {
      console.error("Error updating order status:", err);
      // Revert on error
      setOrders(prev => prev.map(order => 
        order._id === orderId ? { ...order, status: "pending" } : order
      ));
      alert("Error updating order status");
    }
  };

  if (loading) {
    return (
      <div className="admin-orders-container">
        <h2>Live Orders Dashboard</h2>
        <div className="loading">Loading orders...</div>
      </div>
    );
  }

  return (
    <div className="admin-orders-container">
      <h2>Live Orders Dashboard</h2>
      
      {!orders || orders.length === 0 ? (
        <div className="no-orders">
          <p>No orders yet. Waiting for orders...</p>
        </div>
      ) : (
        <div className="orders-list">
          {orders.map((order) => (
            <div key={order._id} className={`order-card status-${order.status}`}>
              <div className="order-header">
                <h3>Table {order.tableNumber}</h3>
                <span className={`status-badge ${order.status}`}>
                  {order.status.toUpperCase()}
                </span>
              </div>
              
              <div className="order-items">
                {order.items && order.items.map((item, index) => (
                  <div key={index} className="order-item">
                    <span className="item-quantity">{item.quantity} x</span>
                    <span className="item-name">{item.name}</span>
                    <span className="item-price">₹{item.price}</span>
                  </div>
                ))}
              </div>
              
              <div className="order-footer">
                <div className="order-total">
                  Total: ₹{order.totalAmount}
                </div>
                <div className="order-actions">
                  {order.status === "pending" && (
                    <button 
                      onClick={() => updateStatus(order._id, "preparing")}
                      className="btn btn-primary"
                    >
                      Start Prep
                    </button>
                  )}
                  {order.status === "preparing" && (
                    <button 
                      onClick={() => updateStatus(order._id, "ready")}
                      className="btn btn-success"
                    >
                      Mark Ready
                    </button>
                  )}
                  {order.status === "ready" && (
                    <button 
                      onClick={() => updateStatus(order._id, "completed")}
                      className="btn btn-complete"
                    >
                      Mark Done
                    </button>
                  )}
                  {(order.status === "pending" || order.status === "preparing") && (
                    <button 
                      onClick={() => updateStatus(order._id, "cancelled")}
                      className="btn btn-danger"
                    >
                      Cancel
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
