// src/components/AdminDashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import { Link } from "react-router-dom";
import "./AdminDashboard.css";

const socket = io("http://localhost:5000");

const AdminDashboard = () => {
  const [menu, setMenu] = useState([]);
  const [newItem, setNewItem] = useState({ name: "", price: "", stock: "", estimatedTime: "" });
  const [editingItem, setEditingItem] = useState(null);

  const fetchMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/menu");
      setMenu(res.data);
    } catch (err) {
      console.error("Error fetching menu:", err);
    }
  };

  useEffect(() => {
    fetchMenu();
    socket.on("menuUpdate", (updatedItem) => {
      if (updatedItem.deleted) {
        setMenu(prev => prev.filter(item => item._id !== updatedItem._id));
      } else {
        setMenu(prev => {
          const itemExists = prev.find(item => item._id === updatedItem._id);
          if (itemExists) {
            return prev.map(item => item._id === updatedItem._id ? updatedItem : item);
          } else {
            return [...prev, updatedItem];
          }
        });
      }
    });

    return () => {
      socket.off("menuUpdate");
    };
  }, []);

  const handleAddItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/admin/menu", newItem);
      setNewItem({ name: "", price: "", stock: "", estimatedTime: "" });
      alert("Item added successfully!");
    } catch (err) {
      console.error("Error adding item:", err);
      alert("Failed to add item.");
    }
  };

  const handleUpdateItem = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/admin/menu/${editingItem._id}`, editingItem);
      setEditingItem(null);
      alert("Item updated successfully!");
    } catch (err) {
      console.error("Error updating item:", err);
      alert("Failed to update item.");
    }
  };

  const handleDeleteItem = async (id) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      try {
        await axios.delete(`http://localhost:5000/api/admin/menu/${id}`);
        alert("Item deleted successfully!");
      } catch (err) {
        console.error("Error deleting item:", err);
        alert("Failed to delete item.");
      }
    }
  };

  return (
    <div className="admin-dashboard-container">
        <div className="admin-nav-bar">
            <h2>Admin Panel</h2>
            <Link to="/admin/orders" className="orders-link">View Live Orders →</Link>
            <Link to="/qr-generator" className="orders-link">Generate QR Codes</Link>
        </div>

        {/* Item Add/Edit Form */}
        <div className="management-card form-section">
            <h3>{editingItem ? "Edit Menu Item" : "Add New Menu Item"}</h3>
            <form onSubmit={editingItem ? handleUpdateItem : handleAddItem} className="menu-form-grid">
              <input
                type="text"
                placeholder="Item Name"
                value={editingItem ? editingItem.name : newItem.name}
                onChange={(e) => editingItem ? setEditingItem({...editingItem, name: e.target.value}) : setNewItem({ ...newItem, name: e.target.value })}
                required
              />
              <input
                type="number"
                placeholder="Price"
                value={editingItem ? editingItem.price : newItem.price}
                onChange={(e) => editingItem ? setEditingItem({...editingItem, price: Number(e.target.value)}) : setNewItem({ ...newItem, price: Number(e.target.value) })}
                required
              />
              <input
                type="number"
                placeholder="Stock"
                value={editingItem ? editingItem.stock : newItem.stock}
                onChange={(e) => editingItem ? setEditingItem({...editingItem, stock: Number(e.target.value)}) : setNewItem({ ...newItem, stock: Number(e.target.value) })}
                required
              />
              <input
                type="number"
                placeholder="Time (mins)"
                value={editingItem ? editingItem.estimatedTime : newItem.estimatedTime}
                onChange={(e) => editingItem ? setEditingItem({...editingItem, estimatedTime: Number(e.target.value)}) : setNewItem({ ...newItem, estimatedTime: Number(e.target.value) })}
                required
              />
              <button type="submit" className="action-save">{editingItem ? "Update Item" : "Add Item"}</button>
              {editingItem && <button type="button" onClick={() => setEditingItem(null)} className="action-cancel">Cancel Edit</button>}
            </form>
        </div>
        
        {/* Menu List */}
        <div className="management-card menu-list-section">
            <h3>Current Menu Items ({menu.length})</h3>
            <ul className="menu-list-admin">
              {menu.map((item) => (
                <li key={item._id} className="menu-item-row">
                    <span className="item-name-col">{item.name}</span>
                    <span className="item-data-col">₹{item.price}</span>
                    <span className="item-data-col">Stock: {item.stock}</span>
                    <span className="item-data-col">Time: {item.estimatedTime}m</span>
                    <div className="item-actions">
                        <button onClick={() => setEditingItem(item)} className="action-edit">Edit</button>
                        <button onClick={() => handleDeleteItem(item._id)} className="action-delete">Delete</button>
                    </div>
                </li>
              ))}
            </ul>
        </div>
    </div>
  );
};

export default AdminDashboard;
