// src/components/Menu.js - Fix the table number handling
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; // Add useNavigate
import axios from "axios";
import { io } from "socket.io-client";
import "./Menu.css";

function Menu() {
  const { tableNumber } = useParams(); // This should get the table number from URL
  const navigate = useNavigate();
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState({});
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [socket, setSocket] = useState(null);

  // Add debug logging to check table number
  useEffect(() => {
    console.log("Table number from URL:", tableNumber);
  }, [tableNumber]);

  // ... rest of your existing useEffect and functions ...

  const placeOrder = async () => {
    if (Object.keys(cart).length === 0) {
      alert("Your cart is empty!");
      return;
    }

    // Debug: Check what data we're sending
    const orderItems = Object.values(cart)
      .filter(item => item && item._id && item.quantity > 0)
      .map(item => ({
        menuId: item._id,
        name: item.name,
        quantity: item.quantity,
        price: item.price
      }));

    console.log("Order Items:", orderItems);
    console.log("Table Number:", tableNumber);

    if (!tableNumber || isNaN(tableNumber)) {
      alert("Invalid table number. Please go back and select a table again.");
      return;
    }

    if (orderItems.length === 0) {
      alert("Cannot place order. Please add valid items to your cart.");
      return;
    }

    const totalAmount = Object.values(cart).reduce((sum, item) => sum + (item.price * item.quantity), 0);

    setIsLoading(true);

    try {
      console.log("Sending order data:", {
        tableNumber: parseInt(tableNumber),
        items: orderItems,
        totalAmount,
        status: "pending"
      });

      const response = await axios.post("http://localhost:5000/api/orders", {
        tableNumber: parseInt(tableNumber), // Ensure it's a number
        items: orderItems,
        totalAmount: parseFloat(totalAmount.toFixed(2)),
        status: "pending"
      });

      console.log("Order response:", response.data);

      if (response.data.success) {
        alert("Order placed successfully! ✅");
        setCart({});
        
        // Emit socket event for real-time update
        if (socket) {
          socket.emit("newOrder", response.data.order);
        }
      } else {
        throw new Error(response.data.message || "Order failed");
      }

    } catch (err) {
      console.error("Order submission error:", err.response?.data || err.message);
      
      let errorMessage = "Order failed. Please try again.";
      if (err.response?.data?.message) {
        errorMessage = err.response.data.message;
      } else if (err.response?.data?.error) {
        errorMessage = err.response.data.error;
      }
      
      alert(`Error: ${errorMessage}`);
    } finally {
      setIsLoading(false);
    }
  };

  // In your JSX, make sure table number is displayed correctly
  return (
    <div className="menu-page-wrapper">
      <div className="menu-container">
        <div className="page-header">
          <div>
            <h1 className="page-title">Hotel Management System</h1>
            <p className="page-subtitle">Menu for Table {tableNumber || "Unknown"}</p>
          </div>
          {/* ... rest of your JSX ... */}
        </div>
        {/* ... rest of your JSX ... */}
      </div>
    </div>
  );
}

export default Menu;
