// src/components/AdminOrders.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import "./AdminOrders.css";

const socket = io("http://localhost:5000");

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/orders");
      setOrders(res.data);
    } catch (err) {
      console.error("Error fetching orders:", err);
    }
  };

  useEffect(() => {
    fetchOrders();

    socket.on("newOrder", (newOrder) => {
      setOrders(prev => [newOrder, ...prev]);
    });

    socket.on("updateOrder", (updatedOrder) => {
      setOrders(prev => prev.map(o => o._id === updatedOrder._id ? updatedOrder : o));
    });

    return () => {
      socket.off("newOrder");
      socket.off("updateOrder");
    };
  }, []);

  const updateStatus = async (id, status) => {
    try {
      await axios.put(`http://localhost:5000/api/orders/${id}`, { status });
    } catch (err) {
      console.error("Error updating order status:", err);
    }
  };

  return (
    <div className="admin-orders-container">
      <h2>Live Orders</h2>
      <div className="orders-grid">
        {orders.length === 0 ? (
          <p>No new orders yet.</p>
        ) : (
          orders.map(order => (
            <div key={order._id} className={`order-card status-${order.status}`}>
              <h4>Table {order.tableNumber}</h4>
              <p className="order-status">Status: <strong>{order.status}</strong></p>
              <ul className="order-items-list">
                {order.items.map(i => (
                  <li key={i._id}>{i.quantity} x {i.menuId.name}</li>
                ))}
              </ul>
              <p>Total: â‚¹{order.totalAmount}</p>
              {order.status !== "completed" && (
                <div className="order-actions">
                  <button onClick={() => updateStatus(order._id, "in-progress")}>In Progress</button>
                  <button onClick={() => updateStatus(order._id, "completed")}>Mark Completed</button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AdminOrders;
