// src/components/AdminOrders.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import "./AdminOrders.css";

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      console.log("Fetching orders...");
      
      const res = await axios.get("http://localhost:5000/api/orders");
      console.log("Orders API response:", res.data);
      
      // Handle different response structures
      let ordersData = [];
      
      if (Array.isArray(res.data)) {
        ordersData = res.data;
      } else if (res.data.orders && Array.isArray(res.data.orders)) {
        ordersData = res.data.orders;
      } else if (res.data.data && Array.isArray(res.data.data)) {
        ordersData = res.data.data;
      } else if (res.data.success && Array.isArray(res.data.orders)) {
        ordersData = res.data.orders;
      }
      
      console.log("Processed orders:", ordersData);
      setOrders(ordersData);
      
    } catch (err) {
      console.error("Error fetching orders:", err);
      // Show sample data for testing
      setOrders([
        {
          _id: "1",
          tableNumber: 1,
          status: "pending",
          items: [
            { name: "Samosa", quantity: 1, price: 10 },
            { name: "Pani Puri", quantity: 2, price: 60 }
          ],
          totalAmount: 130
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();

    // Setup socket connection for real-time updates
    const socket = io("http://localhost:5000");
    
    socket.on("newOrder", (newOrder) => {
      console.log("New order received via socket:", newOrder);
      setOrders(prev => [newOrder, ...prev]);
    });

    socket.on("orderUpdated", (updatedOrder) => {
      console.log("Order updated via socket:", updatedOrder);
      setOrders(prev => prev.map(order => 
        order._id === updatedOrder._id ? updatedOrder : order
      ));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      console.log(`Updating order ${orderId} to ${newStatus}`);
      
      const response = await axios.put(`http://localhost:5000/api/orders/${orderId}`, {
        status: newStatus
      });

      if (response.data.success) {
        // Update local state
        setOrders(prev => prev.map(order => 
          order._id === orderId ? { ...order, status: newStatus } : order
        ));
        
        alert(`Order status updated to ${newStatus}`);
      } else {
        alert("Failed to update order status");
      }
    } catch (err) {
      console.error("Error updating order status:", err);
      alert("Error updating order status");
    }
  };

  if (loading) {
    return (
      <div className="admin-orders-container">
        <h2>üìã Live Orders Dashboard</h2>
        <div className="loading">Loading orders...</div>
      </div>
    );
  }

  return (
    <div className="admin-orders-container">
      <h2>üìã Live Orders Dashboard</h2>
      <p className="orders-count">Total Orders: {orders.length}</p>
      
      {orders.length === 0 ? (
        <div className="no-orders">
          <p>üì≠ No orders yet. Waiting for orders...</p>
        </div>
      ) : (
        <div className="orders-grid">
          {orders.map((order) => (
            <div key={order._id} className={`order-card status-${order.status}`}>
              <div className="order-header">
                <h3>Table {order.tableNumber}</h3>
                <span className={`status-badge ${order.status}`}>
                  {order.status?.toUpperCase() || "PENDING"}
                </span>
              </div>
              
              <div className="order-items">
                <h4>Order Items:</h4>
                {order.items && order.items.map((item, index) => (
                  <div key={index} className="order-item">
                    <span className="item-quantity">{item.quantity} x</span>
                    <span className="item-name">{item.name}</span>
                    <span className="item-price">‚Çπ{item.price}</span>
                  </div>
                ))}
              </div>
              
              <div className="order-footer">
                <div className="order-total">
                  Total: ‚Çπ{order.totalAmount}
                </div>
                <div className="order-actions">
                  {order.status === "pending" && (
                    <button 
                      onClick={() => updateOrderStatus(order._id, "preparing")}
                      className="btn btn-primary"
                    >
                      üç≥ Start Prep
                    </button>
                  )}
                  {order.status === "preparing" && (
                    <button 
                      onClick={() => updateOrderStatus(order._id, "ready")}
                      className="btn btn-success"
                    >
                      ‚úÖ Mark Ready
                    </button>
                  )}
                  {order.status === "ready" && (
                    <button 
                      onClick={() => updateOrderStatus(order._id, "completed")}
                      className="btn btn-complete"
                    >
                      üéâ Mark Done
                    </button>
                  )}
                  {(order.status === "pending" || order.status === "preparing") && (
                    <button 
                      onClick={() => updateOrderStatus(order._id, "cancelled")}
                      className="btn btn-danger"
                    >
                      ‚ùå Cancel
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminOrders;
