// src/components/AdminDashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [menu, setMenu] = useState([]);
  const [orders, setOrders] = useState([]);
  const [newItem, setNewItem] = useState({ name: "", price: "", stock: "", estimatedTime: "" });

  // Load menu & orders
  useEffect(() => {
    fetchMenu();
    fetchOrders();
  }, []);

  const fetchMenu = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/menu");
      setMenu(res.data);
    } catch (err) {
      console.error("Error fetching menu:", err);
    }
  };

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/orders");
      setOrders(res.data);
    } catch (err) {
      console.error("Error fetching orders:", err);
    }
  };

  // Add menu item
  const addItem = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/menu", newItem);
      setNewItem({ name: "", price: "", stock: "", estimatedTime: "" });
      fetchMenu(); // refresh menu list
    } catch (err) {
      console.error("Error adding item:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Admin Dashboard</h2>

      {/* Add New Item */}
      <div style={{ marginBottom: "30px" }}>
        <h3>Add New Menu Item</h3>
        <form onSubmit={addItem}>
          <input
            type="text"
            placeholder="Item Name"
            value={newItem.name}
            onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newItem.price}
            onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
          />
          <input
            type="number"
            placeholder="Stock"
            value={newItem.stock}
            onChange={(e) => setNewItem({ ...newItem, stock: e.target.value })}
          />
          <input
            type="number"
            placeholder="Estimated Time (mins)"
            value={newItem.estimatedTime}
            onChange={(e) => setNewItem({ ...newItem, estimatedTime: e.target.value })}
          />
          <button type="submit">Add Item</button>
        </form>
      </div>

      {/* Menu List */}
      <div>
        <h3>Menu Items</h3>
        {menu.length === 0 ? (
          <p>No items available</p>
        ) : (
          <ul>
            {menu.map((item) => (
              <li key={item._id}>
                {item.name} - ${item.price} | Stock: {item.stock} | Time: {item.estimatedTime} mins
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Orders List */}
      <div style={{ marginTop: "30px" }}>
        <h3>Orders</h3>
        {orders.length === 0 ? (
          <p>No orders yet</p>
        ) : (
          <ul>
            {orders.map((order) => (
              <li key={order._id}>
                Table {order.tableNumber} â†’ {order.items.length} item(s), Total: ${order.totalAmount}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
