// src/components/Menu.js
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { io } from "socket.io-client";
import "./Menu.css";

const socket = io("http://localhost:5000");

function Menu() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tableNumber = queryParams.get("table");

  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState({});

  useEffect(() => {
    axios.get("http://localhost:5000/api/menu")
      .then((res) => setMenu(res.data))
      .catch((err) => console.error(err));

    socket.on("stockUpdate", (updatedItems) => {
      setMenu(prevMenu =>
        prevMenu.map(item => {
          const updated = updatedItems.find(u => u._id === item._id);
          return updated ? updated : item;
        })
      );
    });

    return () => {
      socket.off("stockUpdate");
    };
  }, []);

  const addToCart = (item) => {
    setCart(prevCart => {
      const quantity = (prevCart[item._id]?.quantity || 0) + 1;
      return {
        ...prevCart,
        [item._id]: { ...item, quantity }
      };
    });
  };

  const placeOrder = async () => {
    if (Object.keys(cart).length === 0) {
      alert("Your cart is empty!");
      return;
    }
    const orderItems = Object.values(cart).map(item => ({
      menuId: item._id,
      quantity: item.quantity
    }));

    // Calculate total amount to send to the backend
    const totalAmount = Object.values(cart).reduce((sum, item) => sum + item.price * item.quantity, 0);

    try {
      await axios.post("http://localhost:5000/api/orders", {
        tableNumber: Number(tableNumber),
        items: orderItems,
        totalAmount // Pass total amount to the backend
      });
      alert("Order placed successfully! ✅");
      setCart({});
    } catch (err) {
      console.error(err);
      alert("Failed to place order. Please check stock. ❌");
    }
  };

  const totalAmount = Object.values(cart).reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="menu-page-wrapper">
      <div className="menu-container">
        <h2 className="page-title">Menu for Table {tableNumber}</h2>
        
        <div className="menu-grid">
          {menu.map((item) => (
            <div key={item._id} className="menu-card">
              <h3 className="item-name">{item.name}</h3>
              <div className="item-details-row">
                <span className="item-price">₹{item.price}</span>
                <span className={`item-stock status-${item.stock > 10 ? 'high' : item.stock > 0 ? 'low' : 'out'}`}>
                  Stock: {item.stock} left
                </span>
              </div>
              <p className="item-time">Est. Time: {item.estimatedTime} mins</p>
              
              {item.stock > 0 ? (
                <button className="add-button" onClick={() => addToCart(item)}>
                  Add to Cart
                </button>
              ) : (
                <button className="add-button disabled" disabled>Out of Stock</button>
              )}
            </div>
          ))}
        </div>
      </div>
      
      {/* Sticky Cart Summary */}
      {Object.keys(cart).length > 0 && (
        <div className="sticky-cart-summary">
          <div className="cart-header">
            <h4>Your Order ({Object.keys(cart).length} items)</h4>
          </div>
          <ul className="cart-list">
            {Object.values(cart).map(item => (
              <li key={item._id}>
                {item.name} x {item.quantity} <span>₹{item.price * item.quantity}</span>
              </li>
            ))}
          </ul>
          <div className="cart-footer">
            <span className="cart-total-label">Total:</span>
            <span className="cart-total-amount">₹{totalAmount}</span>
            <button onClick={placeOrder} className="place-order-button">
              Place Order
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Menu;
